from bs4 import BeautifulSoup


class CrawlerDFCFPDF(object):
    def __init__(self):
        self.url = ''

    def crawler_encodeUrl_title(self, url):
        res = requests.get(url)
        soup = BeautifulSoup(res.text, "html.parser")
        tmp = soup.select('script')
        encodeUrl_title_map = {}
        for key in tmp:
            if str(key)[9:21] == 'var initdata':
                tmp_json = json.loads(str(key)[24:len(str(key))-15])    # dict
                data = tmp_json["data"]     #  list
                #print(type(data[0]))   #  dict
                for key in data:       # dict
                    tmp_title = key["title"]
                    stockName = key["stockName"]
                    stockCode = key["stockCode"]
                    orgCode = key["orgCode"]
                    orgName = key["orgName"]
                    orgSName = key["orgSName"]
                    publishDate = key["publishDate"]
                    infoCode = key["infoCode"]
                    indvInduName = key["indvInduName"]
                    author = key["author"]    #    ['11000176123.王席鑫', '11000245033.罗雅婷']
                    encodeUrl = key["encodeUrl"]
                    title = indvInduName + '_' + tmp_title
                    if encodeUrl not in encodeUrl_title_map:
                        encodeUrl_title_map[encodeUrl] = title
        return encodeUrl_title_map

    def crawler_pdfurl(self, url):
        before_url = "https://data.eastmoney.com/report/zw_stock.jshtml?encodeUrl="
        encodeUrl_title_map = self.crawler_encodeUrl_title(url)
        encodeUrl_set = set()
        pdfurl_title = {}
        for key in encodeUrl_title_map:
            title = encodeUrl_title_map[key]
            if key not in encodeUrl_set:
                encodeUrl_set.add(key)
                total_url = before_url + key
                tmp = requests.get(total_url)
                soup = BeautifulSoup(tmp.text,"html.parser")
                span = soup.select('span')
                for ele in span:
                    if str(ele)[70:78] == '<a href=':
                        pdf_url = str(ele)[79:152]
                        print('pdf_url: ', pdf_url)
                        if pdf_url not in pdfurl_title:
                            pdfurl_title[pdf_url] = title
        return pdfurl_title

    def get_url_file(self, url, outputfilepath):
        pdfurl_title = self.crawler_pdfurl(url)
        for key in pdfurl_title:
            title = pdfurl_title[key]
            res_pdf = requests.get(key)
            if '/' in title:
                tmp = ''
                for key in title.split('/'):
                    tmp += key
                title = tmp
            else:
                title = title
            with open(outputfilepath + title + '.pdf', "wb") as fp:
                for chunk in res_pdf.iter_content(chunk_size=1024):
                    if chunk:
                        fp.write(chunk)
            time.sleep(random.uniform(6,9))

