    def process_json_url(self, s, outputfilepath):
        tmp_json = json.loads(s)
        data = tmp_json["data"]   # list
        first_url = ''            # dfcf_stock
        for ele in data:
            title = ele["title"]
            indvInduName = ele["indvInduName"]
            encodeUrl = ele["encodeUrl"]
            url = first_url + encodeUrl
            res = requests.get(url)
            soup = BeautifulSoup(res.text, "html.parser")
            tmp_span = soup.select('span')
            if '/' in title:
                tmp = ''
                for key in title.split('/'):
                    tmp += key
                title = tmp
            else:
                title = title
            for ele in tmp_span:
                if str(ele)[70:78] == '<a href=':
                    pdf_url = str(ele)[79:152]
                    res_pdf = requests.get(pdf_url)
                    with open(outputfilepath + indvInduName + '_' + title + '.pdf', "wb") as fp:
                        for chunk in res_pdf.iter_content(chunk_size=1024):
                            if chunk:
                                fp.write(chunk)
                    time.sleep(random.uniform(6, 9))

